import TimeframeContext from "./timeframe-context";
import React, {useState, useCallback} from "react";

const TimeframeProvider = (props) =>{
    const [selectedTimeframe, setSelectedTimeframe]= useState("daily");

    // const changeTimeframeHandler = (timeframe) =>{};

    const timeframeContext = {
        selectedTimeframe: selectedTimeframe,
        // setSelectedTimeframe: setSelectedTimeframe,
        setSelectedTimeframe: useCallback(setSelectedTimeframe, []),
    }
    return (
        <TimeframeContext.Provider value={timeframeContext}>
            {props.children}
        </TimeframeContext.Provider>
    )
}

export default TimeframeProvider;
