import React, { useState } from "react";
import styles from "./MainCard.module.css";
import Card from "./UI/Card";
import jeremyImage from "../assets/image-jeremy.png";

const MainCard = () => {
  const [timeFrameIsActive, setTimeFrameIsActive] = useState("");

  const clickHandler = (event) => {
    // console.log(event.target.key);
    // el.props.addClass
    setTimeFrameIsActive(event.target.value);
    console.log(timeFrameIsActive);

  };
  const timeFrames = [
    { id: 1, type: "Daily" },
    { id: 2, type: "Weekly" },
    { id: 3, type: "Monthly" },
  ];

  return (
    <Card className={styles["main-card"]}>
      <header>
        <img src={jeremyImage} alt="Jeremy Robson" />
        <article>
          <p>Report for</p>
          <h1>Jeremy Robson</h1>
        </article>
      </header>
      <sub>
        {timeFrames.map((timeFrame) => (
          <p onClick={clickHandler} key={timeFrame.id}>
            {timeFrame.type}
          </p>
        ))}
        {/* <p onClick={clickHandler}>Daily</p>
        <p className={styles.active}>Weekly</p>
        <p>Monthly</p> */}
      </sub>
    </Card>
  );
};

export default MainCard;
