import React, {useState} from "react";
import styles from "./TimeCard.module.css";
import Card from "./UI/Card";
import ellipsis from "../assets/icon-ellipsis.svg";

const TimeCard = (props) => {
  const [bgImage, setBgImage] = useState("");
  //choose color based on title
  let bgColor;
  // let bgImage;
  switch (props.title) {
    case "Work":
      bgColor = "hsl(15, 100%, 70%)";
      import("../assets/icon-work.svg").then((image) =>
      setBgImage(image.default));
      // bgImage = "work";
      break;
    case "Play":
      bgColor = "hsl(195, 74%, 62%)";
      // bgImage = "play";
      break;
    case "Study":
      bgColor = "hsl(348, 100%, 68%)";
      // bgImage = "study";
      break;
    case "Exercise":
      bgColor = "hsl(145, 58%, 55%)";
      // bgImage = "exercise";
      break;
    case "Social":
      bgColor = "hsl(264, 64%, 52%)";
      // bgImage = "social";
      break;
    case "Self Care":
      bgColor = "hsl(43, 84%, 65%)";
      // bgImage = "self-care";
      break;
    default:
      bgColor = "hsl(195, 74%, 62%)";
      // bgImage = require("../assets/icon-work.svg");
  }
  return (
    <li className={styles["time-card"]}>
      <Card>
        <article style={{backgroundColor: bgColor}}>
        {/* {<img src={require(`../assets/icon-${bgImage}.svg`)} alt=""/>} */}
        {/* <img src={`../assets/icon-work.svg`} alt=""/> */}
        <img src={bgImage} alt=""/>
          {/* <div className={styles.background} style={{backgroundColor: bgColor}}></div> */}
          <div className={styles.content}>
            <p>{props.title}</p>
            <p>32hrs</p>
            <img src={ellipsis} alt="more"/>
            <p>Last Week - 36hrs</p>
          </div>
        </article>
      </Card>
    </li>
  );
};

export default TimeCard;
